document.querySelectorAll(".button-copy-cta-to-clipboard").forEach(t=>{t.addEventListener("click",function(t){t.preventDefault();const e=this;var n=e.previousElementSibling.textContent;const l=e.nextElementSibling;navigator.clipboard.writeText(n),l.style.display="flex",e.style.display="none",setTimeout(function(){l.style.display="none",e.style.display="block"},2e3)})});
